package abc;

public class Customer {
	String personname;
	String personproduct;
	int personprice;
	public Customer() {
		// TODO Auto-generated constructor stub
	}
	public String getPersonname() {
		return personname;
	}
	public void setPersonname(String personname) {
		this.personname = personname;
	}
	public String getPersonproduct() {
		return personproduct;
	}
	public void setPersonproduct(String personproduct) {
		this.personproduct = personproduct;
	}
	public int getPersonprice() {
		return personprice;
	}
	public void setPersonprice(int personprice) {
		this.personprice = personprice;
	}
	
}
